let popup=document.querySelector(".popup"),buttonOpen=document.querySelector(".button-open"),buttonClose=popup.querySelector(".close"),personalName=popup.querySelector("[name = personal-data]");buttonOpen.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("popup-animation"),personalName.focus()}),buttonClose.addEventListener("click",function(){popup.classList.remove("popup-animation")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("popup-animation")&&popup.classList.remove("popup-animation")});